import bookService from "../services/book"

export default class {
    static async books(_, res) {
        try {
            const allBooks = await bookService.books()
            res.status(200).json(allBooks)
        } catch (error) {
            res.status(404).json(error)
        }
    }

    static async bookById(req, res) {
        try {
            const id = req.query.id
            const book = await bookService.bookById(id)
            res.status(200).json(book)
        } catch (error) {
            res.status(404).json(error)
        }
    }

    static async bookByTitle(req, res) {
        try {
            const title = req.query.title
            const book = await bookService.bookByTile(title)
            res.status(200).json(book)
        } catch (error) {
            res.status(404).json(error)
        }
    }

    static async addBook(req, res) {
        try {

            if (!req.query.title) {
                res.status(404).send({
                    message: "title not found"
                })
                return
            }

            if (!req.query.author) {
                res.status(404).send({
                    message: "author not found"
                })
                return
            }


            const title = req.query.title
            const editionDate = Date.now()
            const createdAt = Date.now()
            const updatedAt = Date.now()
            const author = Number(req.query.author)

            await bookService.addBook(title, editionDate, createdAt, updatedAt,author)

            res.status(201).json("Book created successfully")
        } catch (error) {
            console.log(error)
            res.status(404).json(error)
        }
    }

    static async updateBook(req, res) {
        try {
            const id = req.query.id
            const body = req.body;

            console.log(id)
            console.log(body)

            await bookService.updateBook(id, body)

            res.status(200).send({
                message: "book has been updated successfully"
            })

        } catch (error) {
            res.status(404).send({
                message: error.message
            })
        }
    }

    static async deleteBook(req, res) {
        try {
            const id = req.query.id
            await bookService.deleteBook(id)
            res.status(204).send()
        } catch (error) {
            res.status(404).send({
                message: error.message
            })
        }
    }


}

import db from "../models"
import authorsService from "./author"

const Book = db.books

/**
 * This class provides all methods to manage CRUD operations on Book Model
 */
export default class {

    /**
     * Select all Books from the database
     *
     * @returns {Promise<Book[]>}
     */
    static async books() {
        try {
            return await Book.findAll()
        } catch (error) {
            throw error
        }
    }


    /**
     * Select a Book its id
     *
     * @param id
     * @returns {Promise<Book>}
     */
    static async bookById(id) {
        try {
            return await Book.findByPk(id)
        } catch (error) {
            throw error
        }
    }

    /**
     * Select a Book by its title
     *
     * @param title
     * @returns {Promise<Book>}
     */
    static async bookByTile(title) {
        try {
            return await Book.findOne({where: {title: title}})
        } catch (error) {
            throw error
        }
    }

    /**
     * Create new Book in the database
     *
     * @param title
     * @param editionDate
     * @param createdAt
     * @param updateAt
     * @param author
     * @returns {Promise<Book>}
     */
    static async addBook(title, editionDate, createdAt, updateAt, author) {
        try {
            console.log("author id  " + author)

            const book = {
                title: title,
                editionDate: editionDate,
                createdAt: createdAt,
                updateAt: updateAt,
                author_id: author
            };

            console.log(book)
            return await Book.create(book)
        } catch (error) {
            throw error
        }
    }

    /**
     * Update an existing Book
     *
     * @param id
     * @param body
     * @returns {Promise<[number, Book[]]>}
     */
    static async updateBook(id, body) {
        try {
            return await Book.update(body, {
                where: {id: id}
            })
        } catch (error) {
            throw error
        }
    }


    /**
     * Delete an existing Book
     *
     * @param id
     * @returns {Promise<number>}
     */
    static async deleteBook(id) {
        try {
            return await Book.destroy({
                where: {id: id}
            })
        } catch (error) {
            throw error
        }
    }

}

import fs from "fs"
import path from "path"
import Sequelize from "sequelize"
import configJson from "../config/config.js"

const basename = path.basename(__filename)
const env = process.env.NODE_ENV ? process.env.NODE_ENV : "development"

const config = configJson[env]

console.log('this is the environment: ', env)
console.log('this is the environment: ', config)
const db = {}

let sequelize
if (config.environment === "production") {
    sequelize = new Sequelize(
        process.env["production"], config
    );

    sequelize = new Sequelize(
        process.env.DB_NAME,
        process.env.DB_USERNAME,
        process.env.DB_PASS, {
            host: process.env.DB_HOST,
            port: process.env.DB_PORT,
            dialect: "postgres",
            dialectOptions: {
                ssl: true,
                native: true
            },
            logging: true
        }
    );
} else {
    sequelize = new Sequelize(
        config.database, config.username, config.password, config
    );
}

fs
    .readdirSync(__dirname)
    .filter((file) => {
        return (file.indexOf('.') != 0 &&
            (file !== basename) && (file.slice(-3) === '.js'))
    })
    .forEach((file) => {
        const model = require(path.join(__dirname, file))(sequelize, Sequelize)
        db[model.name] = model
    });

Object.keys(db).forEach((modelName) => {
    if (db[modelName].associate) {
        db[modelName].associate(db)
    }
})

db.sequelize = sequelize
db.Sequelize = Sequelize

db.books = require("./book")(sequelize, Sequelize)
db.authors = require("./author")(sequelize, Sequelize)

db.authors.hasMany(db.books, {
    foreignKey: {
        name: "author_id",
        allowNull: false
    },
    as: "book"
})

db.books.belongsTo(db.authors, {
    foreignKey: {
        name: "author_id",
        allowNull: false
    },
    as: "author"
})

export default db

